// Prepare context for email composition
const input = $input.first().json;

// Build conversation summary from history
let conversationSummary = '';
if (input.context?.conversation_history) {
  const history = input.context.conversation_history;
  const recent = history.slice(-5);
  conversationSummary = recent.map(m => `${m.role}: ${m.content}`).join('\n');
}

// Extract key topics from transcript
const transcript = input.transcript || '';
const topics = transcript.match(/(?:about|regarding|for|concerning)\s+([^,.!?]+)/i);
const mainTopic = topics ? topics[1].trim() : 'your request';

return [{
  email_address: input.email_address,
  transcript: transcript,
  conversation_summary: conversationSummary,
  main_topic: mainTopic,
  bot_id: input.bot_id,
  session_id: input.session_id,
  received_at: Date.now()
}];

// Format the response for the orchestrator
const agentOutput = $input.first().json;
const preparedData = $('Prepare AI Context').first().json;

const aiOutput = agentOutput.output || agentOutput.text || '';
const toolCalls = agentOutput.toolCalls || [];
const gmailCall = toolCalls.find(t => t.tool === 'Gmail Send');
const emailSent = gmailCall?.result?.success !== false;

let responseText;
if (emailSent && aiOutput) {
  responseText = aiOutput;
} else if (emailSent) {
  responseText = `I've sent the email to ${preparedData.email_address}.`;
} else {
  responseText = `Sorry, I couldn't send the email. Please try again.`;
}

const duration_ms = Date.now() - preparedData.received_at;

return [{
  success: emailSent,
  response_text: responseText,
  tool_result: {
    tool: 'gmail',
    action: 'send',
    recipient: preparedData.email_address,
    subject: gmailCall?.input?.subject || 'Email from Voice Bot',
    status: emailSent ? 'sent' : 'failed'
  },
  metadata: {
    bot_id: preparedData.bot_id,
    session_id: preparedData.session_id,
    duration_ms: duration_ms
  }
}];

// Build immutable log data for Gmail Agent
const input = $('Workflow Input').first().json;
const preparedContext = $('Prepare AI Context').first().json;
const agentOutput = $('AI Agent').first().json;
const formattedResponse = $input.first().json;

// Extract tool calls from agent
let toolCalls = [];
try {
  if (agentOutput.intermediateSteps) {
    toolCalls = agentOutput.intermediateSteps.map(step => ({
      tool: step.action?.tool || 'Gmail Send',
      input: step.action?.toolInput || {},
      output: step.observation || null
    }));
  }
} catch (e) {
  toolCalls = [];
}

// Build immutable log entry
const immutableLog = {
  transcript_exact: input.transcript || '',
  agent_output_raw: agentOutput.output || '',
  tool_calls: toolCalls,
  timestamps: {
    received_at: preparedContext.received_at,
    processed_at: Date.now(),
    logged_at: new Date().toISOString()
  },
  session_id: input.session_id,
  bot_id: input.bot_id,
  classifier_route: 'gmail_agent',
  classifier_intent: 'send_email',
  tts_result: { success: false, audio_sent: false },
  workflow_source: 'gmail_agent',
  error_flags: {
    agent_error: !agentOutput.output,
    email_error: !formattedResponse.success
  },
  // Gmail-specific data
  email_recipient: input.email_address,
  email_result: formattedResponse.tool_result
};

return [immutableLog];
